import Head from "next/dist/next-server/lib/head";
import Image from "next/image";
import styles from "./Projects.module.scss";
import Scaffold from "../../components/scaffold";
import { Grid } from "node_modules/@material-ui/core/index";
import Fade from "react-reveal/fade";

export default function Projects({}) {
  const projects = [
    {
      icon: "/wrlds-logo.svg",
      title: "WRLDS",
      teamSize: "6",
      role: "Founder",
      description:
        "A multi-platform (iOS & React) superapp for digital content creators.",
      website: "https://www.wrlds.io",
      techStack: [
        "react.png",
        "swift.png",
        "graphql.png",
        "node.png",
        "postgres.png",
      ],
    },
    {
      icon: "/khyber-logo.svg",
      title: "Khyber",
      teamSize: "1",
      description:
        "A browser-based DMS for automotive dealers to manage inventory, customers, and deals.",
      role: "Founder",
      website: "https://www.khyber.io",
      techStack: [
        "react.png",
        "kafka.png",
        "redux.png",
        "node.png",
        "postgres.png",
        "graphql.png",
      ],
    },
  ];

  const renderProjects = () => {
    return projects.map((project) => {
      console.log(project.title.toLowerCase());
      return (
        <Grid item xs={12} md={6} lg={6} xl={3}>
          <a href={project.website} target="_blank">
            <div className={styles.projectCard}>
              <div
                className={styles.imageContainer}
                id={project.title.toLowerCase()}
              >
                {project.title === "Khyber" ? <KhyberLogo /> : <WRLDSLogo />}
              </div>
              <div className={styles.contentContainer}>
                <h3>{project.title}</h3>
                <p>{project.description}</p>
                <div className={styles.techStack}>
                  {renderTechStack(project.techStack)}
                </div>
              </div>
            </div>
          </a>
        </Grid>
      );
    });
  };

  const renderTechStack = (stack) => {
    return stack.map((icon) => {
      console.log(icon);
      return <img className={styles.stackIcon} src={`/${icon}`}></img>;
    });
  };

  return (
    <div className="fullTile">
      <Head>
        <title>Projects | Garrett Jester</title>
        <meta name="description" content="Some of my work." />
        <link rel="icon" href="/favicon.ico" />
      </Head>
      <Scaffold>
        <div style={{ width: "100%" }} className="container">
          <Fade bottom>
            <h4>Projects</h4>
            <Grid style={{ marginTop: "15px" }} container spacing={6}>
              {renderProjects()}
            </Grid>
          </Fade>
        </div>
      </Scaffold>
    </div>
  );
}

